package com.vungle.warren.utility;

import android.content.Context;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.util.Base64;
import android.webkit.WebView;

public class ViewUtility {
    private static final int INPUT_DPI = 330;

    public enum Asset {
        close(""),
        cta(""),
        mute(""),
        unMute(""),
        privacy("");
        
        final String base64src;

        private Asset(String str) {
            this.base64src = str;
        }
    }

    public static Bitmap getBitmap(Asset asset, Context context) {
        byte[] decode = Base64.decode(asset.base64src, 0);
        BitmapFactory.Options options = new BitmapFactory.Options();
        options.inDensity = 330;
        options.inTargetDensity = (int) (context.getResources().getDisplayMetrics().density * ((float) options.inDensity));
        return BitmapFactory.decodeByteArray(decode, 0, decode.length, options);
    }

    public static WebView getWebView(Context context) throws InstantiationException {
        try {
            return new WebView(context);
        } catch (Exception e) {
            throw new InstantiationException(e.getMessage());
        }
    }

    public static int dpToPixels(Context context, int i) {
        return (int) ((((float) i) * context.getResources().getDisplayMetrics().density) + 0.5f);
    }
}
